import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import ReactDOM from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';
import ApexCharts from 'https://cdn.jsdelivr.net/npm/apexcharts@3.36.0/+esm';

function ChartComponent() {
	React.useEffect(() => {
		// Ambil data dari store (diset oleh JS object setelah query jalan)
		const rawData = appsmith.store.Find_Sheet11 || [];

		if (!Array.isArray(rawData) || rawData.length === 0) {
			console.warn("Data tidak ditemukan atau kosong");
			return;
		}

		// Transformasi: hitung jumlah saran per 'Layanan Everpro'
		const serviceCounts = {};
		rawData.forEach(item => {
			const service = item["Layanan Everpro"];
			if (service) {
				serviceCounts[service] = (serviceCounts[service] || 0) + 1;
			}
		});

		const categories = Object.keys(serviceCounts);
		const dataCounts = Object.values(serviceCounts);

		// Konfigurasi chart
		const options = {
			chart: {
				type: 'bar',
				height: 350
			},
			series: [{
				name: 'Jumlah Saran',
				data: dataCounts
			}],
			xaxis: {
				categories: categories
			}
		};

		// Render chart
		const chartElement = document.querySelector("#chart");
		if (chartElement) {
			const chart = new ApexCharts(chartElement, options);
			chart.render();
		} else {
			console.error("Chart element not found");
		}
	}, []);

	return (
		<div id="chart"></div>
	);
}

// Render komponen chart ke dalam elemen root
appsmith.onReady(() => {
	ReactDOM.render(<ChartComponent />, document.getElementById("root"));
});
